class Solution {
public:

    map<vector<int>,int>mp;
    int helper(int ind,int k,int jump,int con)
    {
         if(ind<0)return 0;
        if(ind>k+1)return 0;
         if(mp.count({ind,jump,con}))return mp[{ind,jump,con}];

        if(ind==k)
        {
            int tempans = 0;
            if(ind>0 && con==0){
            tempans = tempans + helper(ind-1,k,jump,1);            
            }
            tempans = tempans + helper(ind+pow(2,jump),k,jump+1,0);            
            return 1 + tempans;
        }
        int ans = 0;
        if(ind>0 && con==0){
            ans = ans + helper(ind-1,k,jump,1);            
        }
            ans = ans + helper(ind+pow(2,jump),k,jump+1,0);            
        return mp[{ind,jump,con}] = ans;
    }

    int waysToReachStair(int k) {
        mp.clear();
        int s=1;
        int jump=1;
        return helper(1,k,0,0);
    }
};
