class Solution {
    private int nCr(long n, long r) {
        if(r > n-r)
            r = n-r;
        double temp = 1;
        for(int i=0; i<r; i++)
            temp *= (n-i);
        for(int i=1; i<=r; i++)
            temp /= i;
        // System.out.println(temp);
        return (int)temp;
    }

    public int waysToReachStair(int k) {
        if(k == 0)
            return 2;
        if(k == 1)
            return 4;
        int res = 0;
        k--;
        long jump = 1;
        while(((long)Math.pow(2, jump)-1)-(jump+1) <= k) {
            long j = (long)Math.pow(2, jump)-1;
            long b = j - k;
            // System.out.println(j + " " + b + " " + jump);
            if(j < k) {
                jump++;
                continue;
            }
            res += nCr(jump+1, b);
            jump++;
        }
        return res;
    }
}
