class Solution {
public:
    #define unsigned long long

    unsigned choose(unsigned n, unsigned k) {
      unsigned r = 1;
      unsigned d;
      if (k > n) return 0;
      for (d=1; d <= k; d++) {
        r *= n--;
        r /= d;
      }
      return r;
    }

    long long fact(long long n, long long z)
    {
        long long x = 1;
        for(long long i = 2; i <= n-z; i++)
        {
            x *= i;
        }
        return x;
    }

    int waysToReachStair(int k) {
        if(k == 0)
        {
            return 2;
        }
        else if(k == 1)
        {
            return 4;
        }
        else if(k == 2)
        {
            return 4;
        }
        else if(k == 4)
        {
            return 2;
        }
        long long x = 1;
        long long c = 0;
        while(x < k)
        {
            x *= 2ll;
            c++;
        }
        long long dif = x-k;
        if(dif == 0)
        {
            return 1;
        }
        else if(c+1 >= dif)
        {
            // cout << dif << "\n";
            // 4 = 2
            // long long z = fact(dif, 0);
            // long long y = fact(c+1, dif);
            // long long yz = fact(c+1-dif, 0);
            return choose(c+1, dif);
        }
        else
        {
            return 0;
        }
    }
};
