def find_n(k):
    i = 0
    while 2**i < k:
        i += 1
    return i

class Solution:
    def waysToReachStair(self, k: int) -> int:
        if k <= 8:
            return [2, 4, 4, 3, 2, 4, 6, 4, 1][k]
        n = find_n(k)
        diff = (2**n) - k
        return math.comb(n + 1, diff)
