class Solution {
public:
    map <long long, map <long long, map <long long, int>>> dp;
    int solve(long long i, int k, long long j = 1, int c = 0)
    {
        if(i > k + 1) return 0;
        if(i == k + 1) return 1;
        if(dp[i][j].find(c) != dp[i][j].end()) return dp[i][j][c];
        int res = (i == k);
        res += solve(i + j, k, j * 2, 0);
        if(c == 0)
        {
            res += solve(i - 1, k, j, 1);
        }
        return dp[i][j][c] = res;
    }
    int waysToReachStair(int k) {
        if(k == 0) return 2;
        return solve(1, k);
    }
};
