class Solution {
public:
    int waysToReachStair(int k) {

        int ans=0;

        if(k==0)
            return 2;

        if(k==1)
            return 4;

        int cur=0;

        #define ll long long

        vector<vector<ll>>nCr(32,vector<ll>(32));

        for(int i=0;i<32;i++){
            for(int j=0;j<32;j++){
                if(j==0){
                    nCr[i][j]=1;
                    continue;
                }

                if(i==0)
                {
                    nCr[i][j]=0;
                    continue;
                }

                nCr[i][j]=nCr[i-1][j]+nCr[i-1][j-1];
            }
        }

        k--;
        // cout<<nCr[3][2]<<"\n";
        for(int i=0;i<=30;i++){
            cur+=(1<<i);
            if(cur>=k){
                int d=cur-k;
                if(i+2>=d){
                    // cout<<"cur: "<<cur<<"\n";
                    // cout<<i<<" "<<d<<"\n";
                int add = nCr[i+2][d];
                    // cout<<add<<"\n";
                ans+=add;                    
                }
            }
        }
        return ans;
    }
};
