class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def helper(current,jump,goneBack):
            if current>k+1:
                return 0
            if current<0:
                return 0
            total = 0
            if current==k:
                total+=1
            total+=helper(current+2**jump,jump+1,False)
            if not goneBack:
                total+=helper(current-1,jump,True)
            return total
        return helper(1,0,False)
