#include <bits/stdc++.h>
using namespace std;
typedef long long ll;
#define dbg(x) cout << #x << " = " << x << "\n"
#define pb push_back
#define fi first
#define se second
#define INF 1e18
#define all(x) (x).begin(), (x).end()
#define set_bits(x) __builtin_popcountll(x)
class Solution {
public:
    ll dp[100][2][100];
    ll solve(ll cnt, ll jump, ll flag, ll& k){
        ll curr = (1 << jump) - cnt;
        if(curr == k){
            ll ret = 1;
            if(!flag){
                if(jump == 0){
                    ret++;
                }
            }
            return ret;
        }
        if(curr > k){
            return 0;
        }
        if(dp[cnt][flag][jump] != -1){
            return dp[cnt][flag][jump];
        }
        ll ans = 0;
        if(!flag){
            ans += solve(cnt + 1, jump, 1, k);
        }
        ans += solve(cnt, jump + 1, 0, k);

        return dp[cnt][flag][jump] = ans;
    }
    ll solve2(ll cnt, ll jump, ll flag, ll& k){
        ll curr = (1 << jump) - cnt;
        if(curr == k){
            ll ret = 0;
            if(!flag){
                ret++;
            }
            if(jump == 0){
                ret++;
            }
            return ret;
        }
        if(curr > k){
            return 0;
        }
        if(dp[cnt][flag][jump] != -1){
            return dp[cnt][flag][jump];
        }
        ll ans = 0;
        if(!flag){
            ans += solve2(cnt + 1, jump, 1, k);
        }
        ans += solve2(cnt, jump + 1, 0, k);

        return dp[cnt][flag][jump] = ans;
    }
    int waysToReachStair(int k) {
        ll K = k;
        for(ll i=0;i<100;i++){
            for(ll j=0;j<2;j++){
                for(ll k=0;k<100;k++){
                    dp[i][j][k] = -1;
                }
            }
        }
        int a = (int) solve(0, 0, 0, K);
        for(ll i=0;i<100;i++){
            for(ll j=0;j<2;j++){
                for(ll k=0;k<100;k++){
                    dp[i][j][k] = -1;
                }
            }
        }
        // dbg(a);
        ll K_ = K+1;
        int b = (int) solve2(0, 0, 0, K_);
        // dbg(b);
        return a + b;
    }
};
