using ll=long long;
class Solution {
public:
    const ll mod = 1e9+7;

    ll inv(ll i) {if (i == 1) return 1; return (mod - ((mod / i) * inv(mod % i)) % mod) % mod;}
    ll mod_mul(ll a, ll b) {a = a % mod; b = b % mod; return (((a * b) % mod) + mod) % mod;}
    ll ncr(ll n, ll r,vector<ll>&fact) {
    if (n<r){
        return 0;
    }
    ll ans=fact[n];
    ans=mod_mul(ans,inv(fact[r]));
    ans=mod_mul(ans,inv(fact[n-r]));
    return ans;
}
    int waysToReachStair(int k) {
        ll n=k;
        ll ans=0;
        vector<ll>fact(32,0);
        fact[0]=1;
        ll mod=1e9+7;
        for (ll i=1;i<32;i++){
            fact[i]=fact[i-1]*i;
            fact[i]%=mod;

        }
        for (int i=0;i<31;i++){
            ll d=(1LL<<i);
            if (d<n){
                continue;
            }
            ll rem=d-n;
            ll gaps=i+1;
            ans+=ncr(gaps,rem,fact);
            ans%=mod;


        }
        return ans;

    }
};
