class Solution {
public:
    long long int c(long long int pos, long long int d){
        long long int x = 1;
        for(int i=0;i<d;i++){
            x*=(pos-i);
            x/=(i+1);
        }
        return x;
    }
    int waysToReachStair(int k) {
        if(!k) return 2;
        long long int x = 0, pos = 1;
        int ans = 0;
        while(true){
            if(x+1<k){
                x = 2*x+1;
                pos++;
                continue;
            }
            long long int d = x - k + 1;
            if(d>pos) break;
            ans += c(pos, d);
            x=2*x+1;
            pos++;
        }
        return ans;
    }
};
