class Solution {
public:

    int solve(int jp,long long f,long long sm,long long k,map<array<long long,3>,long long> &dp){
        if(sm<0||sm>2*k||jp>32)return 0;
        if(dp.find({jp,f,sm})!=dp.end())return dp[{jp,f,sm}];
        int ans=0;
        if(f){
            ans+=solve(jp,0,sm-1,k,dp);
        }
        ans+=solve(jp+1,1,sm+(1ll<<jp),k,dp);
        if(sm==k)ans++;
        dp[{jp,f,sm}]=ans;
        return ans;
    }
    int waysToReachStair(int k) {
        if(k==0)return 2;
        map<array<long long,3>,long long> dp;
        int ans=solve(0,1,1ll,k,dp);
        return ans;
    }
};
