class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def ways(i, used, jump):

            if i > k + 1 or i < 0:
                return 0

            way = 0 if i != k else 1
            if not used and i != 0:

                way += ways(i - 1, True, jump) + ways(i + 2**(jump), False, jump + 1)
            else:
                way += ways(i + 2**(jump), False, jump + 1)

            return way
        return ways(1, False, 0)
