class Solution {
public:
  long long C(int a, int b) {
    long long res = 1;
    for (int i = 1, j = a - b + 1; i <= b; ++i, ++j) {
      res = res * j / i;
    }
    return res;
  }

  int waysToReachStair(int k) {
    int t = 0;
    int cur = 1;
    int gap = 1;
    int res = 0;
    while (true) {
      if (cur - gap > k) break;
      int y = cur - k;
      //cout << cur << ", " << gap << endl;
      if (cur >= k) res += C(gap, y);
      ++gap;
      cur += (1 << t);

      ++t;
    }
    return res;
  }
};
