class Solution:
    def __init__(self):
        self.beam_me_up = {}

    def engage(self, k, i, jump, can):
        if i > k + 5:
            return 0

        if jump > 31:
            return 0

        if (i, jump, can) in self.beam_me_up and self.beam_me_up[(i, jump, can)] != -1:
            return self.beam_me_up[(i, jump, can)]

        starship = 1 if i == k else 0
        if can:
            starship += self.engage(k, i - 1, jump, 0)
        if i + (1 << jump) <= k + 1:
            starship += self.engage(k, i + (1 << jump), jump + 1, 1)

        self.beam_me_up[(i, jump, can)] = starship

        return starship

    def waysToReachStair(self, k):
        return self.engage(k, 1, 0, 1)
