class Solution {
public:
    int waysToReachStair(int k) {

                  using ll = long long ;

                   ll ans = 0 , x = 0 , y = 0; 
                   vector<vector<vector<ll>>> dp(35  , vector<vector<ll>>(35, vector<ll>(3 , -1)));

              function<ll(ll , ll , ll , vector<vector<vector<ll>>>&)>func = [&](ll jump , ll sub , ll f , vector<vector<vector<ll>>>&dp) -> ll{


                           if(jump > 31) return 0 ;

                          if(dp[jump][sub][f] != -1) return dp[jump][sub][f] ;

                      ll ret = 0 ;  
                      bool ff = (1LL << jump) - sub  == k ;
                                 ret += func(jump + 1 , sub  , 0 , dp) ; 
                                 if(!f) ret += func(jump , sub + 1 , 1 , dp) ;
                      return dp[jump][sub][f] =ff +  ret ;


              };

              ans += func(0 , 0 , 0 ,dp);

