class Solution:
    def waysToReachStair(self, k: int) -> int:
        d=dict()
        print("(k,jump,i,d,p,v,a)")
        def call(k,jump,i,d,p,v,a):
            # print(k,jump,i,d,p,v,a)
            if i>k+1:
                return 0
            if (i,jump,p) in d.keys():
                return d[(i,jump,p)]
            x=0
            if i==k:
                x+=1
            if i>0 and p==0:
                x+=call(k,jump,i-1,d,1,v,a+1)
            x+=call(k,jump+1,i+v,d,0,2*v,a+1)
            d[(i,jump,p)]=x
            # print(d)
            return x
        x=call(k,0,1,d,0,1,0)
        return x
