struct best_hash {
	static uint64_t splitmix64(uint64_t x) {

		x += 0x9e3779b97f4a7c15;
		x = (x ^ (x >> 30)) * 0xbf58476d1ce4e5b9;
		x = (x ^ (x >> 27)) * 0x94d049bb133111eb;
		return x ^ (x >> 31);
	}

	size_t operator()(uint64_t x) const {
		static const uint64_t FIXED_RANDOM = chrono::steady_clock::now().time_since_epoch().count();
		return splitmix64(x + FIXED_RANDOM);
	}
};

/* ------------------------------ Let The Game Begin ---------------------------- */

const int N = 5e4 + 5;

ll v[4 * N][5]; // {[first,last],[first,secondLast],[second,last],[second,secondLast], maxSum}
ll x, y, z;

vii arr;

void build(int id, int cx, int cy) {
	if (cx == cy) {
		v[id][0] = v[id][4] = max(0,arr[cx]);
        v[id][1] = v[id][2] = v[id][3] = 0;
		return;
	}

	int mid = (cx + cy) / 2;
	build(2 * id, cx, mid);
	build(2 * id + 1, mid + 1, cy);

	v[id][0] = max({v[2 * id][0] + v[2 * id + 1][2], v[2 * id][1] + v[2 * id + 1][0]});
	v[id][1] = max({v[2 * id][0] + v[2 * id + 1][3], v[2 * id][1] + v[2 * id + 1][1]});
	v[id][2] = max({v[2 * id][2] + v[2 * id + 1][2], v[2 * id][3] + v[2 * id + 1][0]});
	v[id][3] = max({v[2 * id][2] + v[2 * id + 1][3], v[2 * id][3] + v[2 * id + 1][1]});
	v[id][4] = max({v[id][0], v[id][1], v[id][2], v[id][3]});
}

void upd(int id, int cx, int cy) {
	if (x < cx or x > cy) return;
	if (cx == cy) {
		v[id][0] = v[id][4] = z;
		return;
	}


	int mid = (cx + cy) / 2;
	upd(2 * id, cx, mid);
	upd(2 * id + 1, mid + 1, cy);

	v[id][0] = max({v[2 * id][0] + v[2 * id + 1][2], v[2 * id][1] + v[2 * id + 1][0]});
	v[id][1] = max({v[2 * id][0] + v[2 * id + 1][3], v[2 * id][1] + v[2 * id + 1][1]});
	v[id][2] = max({v[2 * id][2] + v[2 * id + 1][2], v[2 * id][3] + v[2 * id + 1][0]});
	v[id][3] = max({v[2 * id][2] + v[2 * id + 1][3], v[2 * id][3] + v[2 * id + 1][1]});
	v[id][4] = max({v[id][0], v[id][1], v[id][2], v[id][3]});

}

class Solution {
public:
	int maximumSumSubsequence(vector<int>& nums, vector<vector<int>>& queries) {
		int n = sz(nums);
		arr = nums;
		build(1, 0, n - 1);

		ll ans = 0;
		for (auto &a : queries) {
			x = a[0], z = max(0,a[1]);
			upd(1, 0, n - 1);
			ans = (ans + v[1][4]) % mod;
		}
		return ans;
	}
};
