const int MAX = 2e5+5;
ll st[MAX], stL[MAX], stR[MAX], stLR[MAX];

void upd(int i, int l, int r, int k, int val){
    if (l == r){
        st[i] = max(0, val);
        return;
    }
    int m = l+(r-l)/2;
    if (k <= m) upd(2*i, l, m, k, val);
    else upd(2*i+1, m+1, r, k, val);
    st[i] = max(st[2*i]+stL[2*i+1], stR[2*i]+st[2*i+1]);
    stL[i] = max(stLR[2*i]+st[2*i+1], stL[2*i]+stL[2*i+1]);
    stR[i] = max(st[2*i]+stLR[2*i+1], stR[2*i]+stR[2*i+1]);
    stLR[i] = max(stL[2*i]+stLR[2*i+1], stLR[2*i]+stR[2*i+1]);
}

class Solution {
public:
    int maximumSumSubsequence(vector<int>& v, vector<vector<int>>& qu) {
        int n = siz(v);
        for (int i = 0; i <= 4*n; i++) st[i] = stL[i] = stR[i] = stLR[i] = 0;
        for (int i = 0; i < n; i++){
            upd(1, 0, n-1, i, v[i]);
        }
        ll ans = 0;
        for (int i = 0; i < siz(qu); i++){
            int p = qu[i][0], val = qu[i][1];
            upd(1, 0, n-1, p, val);
            //deb(st[1]);
            ans = (ans+st[1])%MOD;
        }
        return (int)ans;
    }
};
