struct node {
    ll val[2][2];
};

node from_scalar(ll val) {
    node res;
    FOR(i, 2) FOR(j, 2) {
        res.val[i][j]=0;
        if(i==1 && j==1) domax(res.val[i][j], val);
    }
    return res;
}

node unite(node a, node b) {
    node res;
    FOR(i, 2) FOR(j, 2) res.val[i][j]=0;

    FOR(la, 2) FOR(ra, 2) FOR(lb, 2) FOR(rb, 2) {
        if(!ra || !lb) domax(res.val[la][rb], a.val[la][ra]+b.val[lb][rb]);
    }

    return res;
}

node neutral() {
    node res;
    FOR(i, 2) FOR(j, 2) res.val[i][j]=0;
    return res;
}

ll get_best(node a) {
    ll r=0;
    FOR(i, 2) FOR(j, 2) domax(r, a.val[i][j]);
    return r;
}

using segtree=atcoder::segtree<node, unite, neutral>;

const ll MOD=1'000'000'007;

class Solution {
public:
    int maximumSumSubsequence(vector<int>& a, vector<vector<int>>& queries) {
        const int n=a.size();

        vector<node> V(n);
        FOR(i, n) V[i]=from_scalar(a[i]);
        segtree ST(V);

        ll result=0;
        for(auto q: queries) {
            const int pos=q[0], x=q[1];
            ST.set(pos, from_scalar(x));

            node tmp=ST.all_prod();
            ll val=get_best(tmp);
            result+=val;
            result%=MOD;
        }

        return result;
    }
};
