class Solution {
public:
    int r[100005][30];
    int minimumDifference(vector<int>& a, int T) {
        int zd = 0, n = a.size(), i, j, k, you, now, res = 2000000000, M = 30;
        for (j = 0; j < M; ++j) r[n][j] = n;
        for (i = n - 1; i >= 0; --i)
            for (j = 0; j < M; ++j)
                if (!(a[i] & (1<<j))) r[i][j] = i;
                else r[i][j] = r[i + 1][j];

        for (i = 0; i < n; ++i){
            now = a[i];
            res = min(res, (int)labs(now - T));
            j = i;
            while (j + 1 < n && now != 0){
                you = n;
                for (k = 0; k < M; ++k)
                    if (now & (1<<k))
                        you = min(you, r[j + 1][k]);
                j = you;
                if (j == n) break;
                now &= a[j];
                res = min(res, (int)labs(now - T));
            }
        }

        return res;
    }
};
