class Solution {
public:
#define sz(x) int(x.size())
#define all(x) x.begin(), x.end()
#define db double
#define ll long long
#define ar array
template<typename T> bool ckmin(T& a, const T& b) { return a > b ? a = b, 1 : 0; }
template<typename T> bool ckmax(T& a, const T& b) { return a < b ? a = b, 1 : 0; }
template<typename it, typename bin_op>
struct sparse_table {
    using T = typename remove_reference<decltype(*declval<it>())>::type;
    vector<vector<T>> t; bin_op F;

    sparse_table(it first, it last, bin_op op) : t(1), F(op) {
        int n = distance(first, last);
        t.assign(32-__builtin_clz(n), vector<T>(n));
        t[0].assign(first, last);
        for (int i = 1; i < sz(t); i++)
            for (int j = 0; j < n-(1<<i)+1; j++)
                t[i][j] = F(t[i-1][j], t[i-1][j+(1<<(i-1))]);
    }

    T query(int l, int r) {
        int h = 31 - __builtin_clz(r-l+1);
        return F(t[h][l], t[h][r-(1<<h)+1]);
    }
};

    int minimumDifference(vector<int>& nums, int k) {
        int n = sz(nums);
        sparse_table st(all(nums), [&](int x, int y) { return x & y; });
        int ans2 = 1e9;
        for (int i = 0; i < n; i++) {
            if (nums[i] <= k) {
                ckmin(ans2, k - nums[i]);
                continue;
            }
            int l = i, r = n-1, ans = i;
            while (l <= r) {
                int mid = (l+r)/2;
                if (st.query(i, mid) > k) ans = mid, l = mid+1;
                else r = mid-1;
            }
            ckmin(ans2, abs(k - st.query(i, ans)));
            if (ans + 1 < n) ckmin(ans2, abs(k - st.query(i, ans + 1)));
        }
        return ans2;
    }
};
