class Solution {
	public int minimumDifference(int[] nums, int k) {
		int n = nums.length;
		int[] a = {};
		int min = Integer.MAX_VALUE;
		for(int i = 0;i < n;i++){
			int v = nums[i];
			int[] na = new int[a.length+1];
			int p = 0;
			na[p++] = v;
			min = Math.min(min, Math.abs(v-k));
			for(int j = 0;j < a.length;j++){
				int w = a[j] & v;
				if(na[p-1] != w){
					na[p++] = w;
					min = Math.min(min, Math.abs(w-k));
				}
			}
			a = Arrays.copyOf(na, p);
		}
		return min;
	}
}
