class Modified_segTree{
	public:
		vector<int> sTree;
		vii a;
		int n;

		Modified_segTree(vii a){
			this->a=a;
			n=a.size();
		}
		string combine(string s1,string s2){
			for(auto ch:s2){
				if(s1.find(ch)==string::npos)s1+=ch;
			}
			return s1;
		}
		void make_tree(int v,int l,int r){
			if(l==r){
				sTree[v]=a[l];
				return;
			}
			int mid=(l+r)/2;
			make_tree(2*v+1,l,mid);
			make_tree(2*v+2,mid+1,r);
			sTree[v]=sTree[2*v+1]&sTree[2*v+2];
		}
		void build(){
			sTree.clear();
			sTree.resize(4*n);
			make_tree(0,0,n-1);
		}
		void modify(int v,int l,int r,int pos){
			if(l==r){
				sTree[v]=a[l];
				return;
			}
			int mid=(l+r)/2;
			if(pos<=mid)modify(2*v+1,l,mid,pos);
			else modify(2*v+2,mid+1,r,pos);
			sTree[v]=sTree[2*v+1]&sTree[2*v+2];
		}
		void update(int pos,char val){
			a[pos]=val;
			modify(0,0,n-1,pos);
		}
		int find_value(int v,int l,int r,int st,int ed){
			if((l==st)&&(r==ed))return sTree[v];
			int mid=(l+r)/2;
			if(ed<=mid)return find_value(2*v+1,l,mid,st,ed);
			if(st>mid)return find_value(2*v+2,mid+1,r,st,ed);
			return (find_value(2*v+1,l,mid,st,mid)&find_value(2*v+2,mid+1,r,mid+1,ed));
		}
		int get_value(int l,int r){
			return find_value(0,0,n-1,l,r);
		}
};



class Solution {
public:
    int minimumDifference(vector<int>& a, int k) {
        Modified_segTree sT(a);
        sT.build();
        int i,n=sz(a),ans=abs(k-a[0]),st,ed,mid,AND;
        for(i=1;i<n;i++){
            st=0;
            ed=i;
            while(st<=ed){
                mid=(st+ed)/2;
                AND=sT.get_value(mid,i);
                ans=min(ans,(int)(abs(AND-k)));
                if(AND==k)break;
                if(AND>k)ed=mid-1;
                else st=mid+1;
            }
        }
        return ans;
    }
};
