class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        vector<int>arr(32);
        int n=nums.size();
        int i=0;
        int j=0;
        int ini=INT_MAX;
        int ans=1e9;
        while(j<n)
        {
            //cout<<ini<<" "<<i<<" "<<j<<" "<< ans<<endl;
            //cout<<ini<<" ";
            for(int x=31;x>=0;x--)
            {
                if(nums[j]&(1<<x))
                    continue;
                arr[x]++;
                ini=ini&~(1<<x);
            }
            //cout<<ini<<" ";
            ans=min(ans,abs(k-ini));
            while(ini<k && i<n && ini>=0)
            {
                ans=min(ans,abs(k-ini));
                cout<<ini<<" "<<i<<" "<<j<<endl;
                for(int x=31;x>=0;x--)
                {
                    if(nums[i]&(1<<x))
                        continue;
                    arr[x]--;
                    if(arr[x]==0)
                    ini|=(1<<x);
                    //ans=min(ans,abs(k-ini));
                    //cout<<ini<<" ";
                }
                i++;
            }

            ans=min(ans,abs(k-ini));
            j++;
        }
        return ans;
    }
};
