class Solution {
    public int minimumDifference(int[] nums, int k) {
          int[] zeros = new int[32];

        class Bitwise {
            void minus(int x) {
                for (int i = 0; i < 32; ++i) {
                    if (((x >> i) & 1) == 0) {
                        zeros[i] -= 1;
                    }
                }
            }

            void plus(int x) {
                for (int i = 0; i < 32; ++i) {
                    if (((x >> i) & 1) == 0) {
                        zeros[i] += 1;
                    }
                }
            }

            int eval() {
                int res = 0;
                for (int i = 0; i < 32; ++i) {
                    if (zeros[i] == 0) {
                        res ^= (1 << i);
                    }
                }
                return res;
            }
        }

        Bitwise bitwise = new Bitwise();

        int i = 0;
        int n = nums.length;
        int ans = Integer.MAX_VALUE;

        for (int j = 0; j < n; ++j) {
            bitwise.plus(nums[j]);
            int x = bitwise.eval();
            ans = Math.min(ans, Math.abs(x - k));
            while (i < j && x < k) {
                bitwise.minus(nums[i]);
                ++i;
                x = bitwise.eval();
                ans = Math.min(ans, Math.abs(x - k));
            }
        }

        return ans;
    }
}
