class Solution {
public:
    static void m(int x, vector<int>& z) {
        for (int i = 0; i < 32; ++i) {
            if (((x >> i) & 1) == 0) {
                z[i] -= 1;
            }
        }
    }

    static void p(int x, vector<int>& z) {
        for (int i = 0; i < 32; ++i) {
            if (((x >> i) & 1) == 0) {
                z[i] += 1;
            }
        }
    }

    static int e(vector<int>& z) {
        int res = 0;
        for (int i = 0; i < 32; ++i) {
            if (z[i] == 0) {
                res ^= (1 << i);
            }
        }
        return res;
    };


    int minimumDifference(vector<int>& nums, int k) {
        vector<int> z(32, 0);

        int i = 0;
        int n = nums.size();
        int ans = INT_MAX;

        for (int j = 0; j < n; ++j) {
            p(nums[j], z);
            int x = e(z);
            ans = min(ans, abs(x - k));
            while (i < j && x < k) {
                m(nums[i], z);
                ++i;
                x = e(z);
                ans = min(ans, abs(x - k));
            }
        }

        return ans;
    }
};
