#define pll pair<long long, long long>
#define sz(x) ((int)(x).size())
#define pb push_back
#define ppb pop_back
#define mp make_pair
#define ff first
#define ss second
#define all(x) (x).begin(), (x).end()

typedef long long ll;
class Solution {
public:
    void addNumsi(int a,vint& vc){
        for(int i=0;i<31;i++){
            if(a & (1<<i)){
                vc[i]++;
            }
        }
    }
    void remNumsi(int a,vint& vc){
        for(int i=0;i<31;i++){
            if(a & (1<<i) ){
                vc[i]--;
            }
        }
    }
    int getAnd(vint& vc,int len){
        if(len==0)  return 0;
        int tmp = 0 ;
        for(int i=0;i<31;i++){
            if(vc[i]!=len)  continue;
            tmp = tmp | (1<<i) ;
        }
        return tmp;
    }
    int minimumDifference(vector<int>& nums, int k) {
        int mini = 1e9;
        for(auto it : nums) mini=min(mini,abs(k-it));
        vint vc(31,0);
        int s=0;
        for(int e=0;e<sz(nums);e++){
            addNumsi(nums[e],vc);
            int subAnd = getAnd(vc,e-s+1);
            mini = min(mini,abs(subAnd-k));
            while(s<e && subAnd<k ){
                remNumsi(nums[s],vc);
                s++;
                subAnd=getAnd(vc,e-s+1);
                mini = min(mini,abs(subAnd-k));
            }
            // cout<<s<<"   "<<e<<" "<<subAnd<<endl;
            mini = min(mini,abs(subAnd-k));
        }
        return mini;
    }
};
