class CustomSegTree {
public:
    CustomSegTree(const vector<int>& arr) {
        n = arr.size();
        seg_tree.resize(4 * n);
        build(arr, 0, 0, n - 1);
    }

    int query(int left, int right) {
        return range_query(0, 0, n - 1, left, right);
    }

private:
    int n;
    vector<int> seg_tree;

    void build(const vector<int>& arr, int node, int start, int end) {
        if (start == end) {
            seg_tree[node] = arr[start];
        } else {
            int mid = (start + end) / 2;
            build(arr, 2 * node + 1, start, mid);
            build(arr, 2 * node + 2, mid + 1, end);
            seg_tree[node] = seg_tree[2 * node + 1] & seg_tree[2 * node + 2];
        }
    }

    int range_query(int node, int start, int end, int left, int right) {
        if (right < start || end < left) {
            return INT_MAX;
        }
        if (left <= start && end <= right) {
            return seg_tree[node];
        }
        int mid = (start + end) / 2;
        int left_res = range_query(2 * node + 1, start, mid, left, right);
        int right_res = range_query(2 * node + 2, mid + 1, end, left, right);
        return left_res & right_res;
    }
};

class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int n = nums.size();
        CustomSegTree seg_tree(nums);
        int min_diff = INT_MAX;

        auto custom_abs = [](int a, int b) {
            return abs(a - b);
        };

        for (int i = 0; i < n; ++i) {
            int left = i - 1, right = n - 1;
            while (right - left > 1) {
                int mid = (left + right) / 2;
                if (seg_tree.query(i, mid) >= k) {
                    left = mid;
                } else {
                    right = mid;
                }
            }
            if (left != i - 1) {
                min_diff = min(min_diff, custom_abs(seg_tree.query(i, left), k));
            }
            min_diff = min(min_diff, custom_abs(k, seg_tree.query(i, right)));
        }
        return min_diff;
    }
};
