class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int n = nums.size();
        int bits[n + 1][32];
        for (int i = 0; i < n; i++){
            for (int j = 0; j < 32; j++){
                bits[i][j] = (bool)((1ll << j) & nums[i]);
            }
        }

        for (int i = 1; i < n; i++){
            for (int j = 0; j < 32; j++){
                bits[i][j] += bits[i - 1][j];
            }
        }

        // for (int i = 0; i < n; i++){
        //     for (int j = 0; j < 32; j++){
        //         cout << bits[i][j] << ' ';
        //     }
        //     cout << endl;
        // }

        int ans = INT_MAX;

        for (int i = 0; i < n; i++){
            int lo = i , hi = n - 1 , mid , idx = i;

            while (lo <= hi){
                mid = (lo + hi) / 2;

                int num = 0;
                for (int j = 0; j < 32; j++){
                    int ck = bits[mid][j];
                    if (i) ck -= bits[i - 1][j];

                    if (ck == mid - i + 1) num += (1ll << j);
                }
                // cout << num << endl;
                if (num > k){
                    ans = min(ans , abs(num - k));
                    lo = mid + 1;
                }
                else{
                    idx = mid;
                    ans = min(ans , abs(num - k));
                    hi = mid - 1;
                }
            }
            if (idx > i){
                cout << "Ok";
                int num = 0;
                for (int j = 0; j < 32; j++){
                    int ck = bits[idx - 1][j];
                    if (i) ck -= bits[i - 1][j];

                    if (ck == idx - i) num += (1ll << j);
                }
                ans = min(ans , abs(num - k));
            }
        }

        return ans;
    }
};
