#define ll long long
class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int n = (int)nums.size();
        vector <vector <int>> pref(30, vector <int> (n + 1));

        for(int j = 0; j < 30; j++) {
            for(int i = 0; i < n; i++) {
                if ((nums[i] >> j) & 1) {
                    pref[j][i + 1] = pref[j][i];
                    pref[j][i + 1] += 1;
                }
            }
        }

        ll ans = 2e9;

        for(int i = 0; i < n; i++) {
            ll l = i + 1, r = n;
            while (l < r) {
                ll mid = (l + r) / 2;
                int temp = 0;
                for(int j = 0; j < 30; j++) {
                    if (pref[j][mid] - pref[j][i] == mid - i) {
                        temp ^= (1 << j);
                    }
                }
                if (temp > k) {
                    l = mid + 1;
                } else {
                    r = mid;
                }
            }
            int fin = 0;
            for(int j = 0; j < 30; j++) {
                if (pref[j][l] - pref[j][i] == l - i) {
                    fin ^= (1 << j);
                }
            }
            ans = min(ans, 1ll * abs(k - fin));



            l = i + 1, r = n;
            while (l < r) {
                ll mid = (l + r + 1) / 2;
                int temp = 0;
                for(int j = 0; j < 30; j++) {
                    if (pref[j][mid] - pref[j][i] == mid - i) {
                        temp ^= (1 << j);
                    }
                }
                if (temp >= k) {
                    l = mid;
                } else {
                    r = mid - 1;
                }
            }
            fin = 0;
            for(int j = 0; j < 30; j++) {
                if (pref[j][l] - pref[j][i] == l - i) {
                    fin ^= (1 << j);
                }
            }
            ans = min(ans, 1ll * abs(k - fin));
            //cout<<i<<" "<<ans<<endl;
        }

        return (int)ans;
    }
};
