class Solution:
    def minimumDifference(self, nums: List[int], k: int) -> int:
        res = inf
        cnt = [0] * 31
        i = 0
        for j, num in enumerate(nums):
            value = 0
            for d in range(31):
                cnt[d] += (num >> d) & 1
                if cnt[d] == j - i + 1:
                    value |= (1 << d)

            res = min(res, abs(k - value))
            while i < j and value < k:
                value = 0
                for d in range(31):
                    cnt[d] -= (nums[i] >> d) & 1
                    if cnt[d] == j - i:
                        value |= (1 << d)

                res = min(res, abs(k - value))
                i += 1

        return res
