class Solution {
public:
    int solve(int l, int r, int x, vector<int>& nums, int k){
        int ans;
        int val = nums[l];
        for(int i=l + 1;i<=r;i++){
            val &= nums[i];
        }
        ans=abs(k-val);
        if(x<0){
            return ans;
        }
        for(int i=l+1, s=l; i<=r; i++){
            if((nums[i] & (1<<x)) != (nums[i-1] & (1<<x))){
                ans=min(ans, solve(s, i-1, x-1, nums, k));
                s=i;
            }
            if(i==r){
                ans=min(ans, solve(s, i, x-1, nums, k));
            }
        }

        return ans;
    }
    int minimumDifference(vector<int>& nums, int k) {
        int x = -1;
        for(int i=0;i<32;i++){
            if(k&(1<<i)) x=i;
        }
        int chk = INT_MAX;
        for(int i=0;i<nums.size();i++){
            int x=nums[i];
            chk=min(chk, abs(k-x));
            for(int j=i+1; j<i+500;j++){
                if(j>=nums.size()) break;
                x &= nums[j];
                chk=min(chk, abs(k-x));
            }
        }
        return min(chk, solve(0, nums.size()-1, x, nums, k));
    }
};
