class SegTree {
public:
    int n = 0;
    vector<int> segTree;
    SegTree(vector<int>& nums) {
        n = nums.size();
        segTree.resize(4*n);
        build(nums, 0, 0, n-1);

    }

    void build(vector<int>& nums, int index, int left, int right){
        if (left == right){
            segTree[index] = nums[left];
        }
        else {
            int mid = left + (right-left)/2;
            build(nums, 2*index+1, left, mid);
            build(nums, 2*index+2, mid+1, right);
            segTree[index] = segTree[2*index+1] & segTree[2*index+2];
        }
    }

    void update(int index, int val) {
        index += n;
        segTree[index] = val;

        while(index > 1){
            index = index >> 1;
            segTree[index] = segTree[index << 1] & segTree[(index << 1) | 1];
        }

    }

    int query(int low, int high) {
        return queryUtil(0, 0, n-1, low, high);
    }

    int queryUtil(int index, int left, int right, int low, int high){
        if (high < left || right < low)return INT_MAX;
        if (low <= left && right <= high)return segTree[index];
        int mid = left + (right-left)/2;
        int temp1 = queryUtil(2*index+1, left, mid, low, high);
        int temp2 = queryUtil(2*index+2, mid+1, right, low, high);
        return temp1 & temp2;
    }
};
class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int n = nums.size();
        if (n == 1){
            return abs(k-nums[0]);
        }
        SegTree s(nums);
        int ans = INT_MAX;
        for (int i=0; i<n; i++){
            int left = i-1, right = n-1;
            while(right > left + 1){
                int mid = left + (right-left)/2;
                if (s.query(i, mid) >= k){
                    left = mid;
                }
                else {
                    right = mid;
                }
            }
            if (left != i-1){
                ans = min(ans, abs(s.query(i, left)-k));
            }
            ans = min(ans, abs(k - s.query(i, right)));
        }
        return ans;
    }
};
