class Solution:
    def minimumDifference(self, nums: List[int], k: int) -> int:
        # answer=float("inf")
        # for i in range(0,len(nums)):
        #     answer=min(answer,abs(nums[i]-k))
        # return answer    
        n=len(nums)
        dit={}
        answer=[float('inf')]
        def dfs(index,calc):

            answer[0]=min(answer[0],abs(calc-k))
            if index>=n-1:
                return
            if (index,calc) in dit:
                return
            dfs(index+1,calc&nums[index+1])
            dfs(index+1,nums[index+1])
            dit[(index,calc)]=1
        dfs(0,nums[0])    
        return answer[0]
