class Solution {
public:
  int zeros[32] = { 0 };
  void remove(int x) {
    for (int i = 0; i < 32; ++i) {
      if (((x >> i) & 1) == 0) {
        zeros[i] -= 1;
      }
    }
  }
    void add(int x) {
      for (int i = 0; i < 32; ++i) {
        if (((x >> i) & 1) == 0) {
          zeros[i] += 1;
        }
      }
    }
    int cacl() {
      int res = 0;
      for (int i = 0; i < 32; ++i) {
        if (zeros[i] == 0) {
          res ^= (1 << i);
        }
      }
      return res;
      }
  int minimumDifference(vector<int>& nums, int k) {




    int i = 0, n = nums.size(), ans = INT_MAX;

    for (int j = 0; j < n; ++j) {
      add(nums[j]);
      int x = cacl();
      ans = min(ans, abs(x - k));

      while (i < j && x < k) {
        remove(nums[i]);
        ++i;
        x = cacl();
        ans = min(ans, abs(x - k));
      }
    }

    return ans;
  }
};
