class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        vector<vector<int>> bits(32);
        int n = nums.size();
        for (int i = 0; i<n; i++){

            for(int j = 0; j<32; j++){
                if ((nums[i]>>j)%2==0) bits[j].push_back(i);
            }
        }
        int st = 0, en = 0;
        int ans = INT_MAX;
        int temp = nums[0];
        while(en<n){
            temp = temp&nums[en];
            ans = min(ans, abs(k-temp));
            while (st<en&&temp<k){
                for(int j = 0; j<32; j++){
                    if ((temp>>j)%2==0){auto itt = upper_bound(bits[j].begin(), bits[j].end(), st);
                    auto itt2 = lower_bound(bits[j].begin(), bits[j].end(), st);
                    if (itt==bits[j].end() || (itt!=bits[j].end()&&*itt>en)){
                        // cout<<"working"<<j<<" "<<en<<" "<<st<<endl;
                        if (itt2!=bits[j].end()&&*itt2<=st){
                            temp = temp|(1<<j);
                            // cout<<"working"<<j<<" "<<en<<" "<<st<<endl;
                        }
                    }}
                }
                ans = min(ans, abs(k-temp));    
                st++;
            }
            ans = min(ans, abs(k-temp));    
            // cout<<st<<" "<<en<<" "<<temp<<"\n";
            en++;
        }
        return ans;
    }
};
