class Solution {
public:
    long n, t[4*100000];

    void build(vector<int>&a, int v, int tl, int tr) {
        if (tl == tr) {
            t[v] = a[tl];
        } else {
            int tm = (tl + tr) / 2;
            build(a, v*2, tl, tm);
            build(a, v*2+1, tm+1, tr);
            t[v] = t[v*2] & t[v*2+1];
        }
    }

    int bitWiseAnd(int v, int tl, int tr, int l, int r) {
      if (l > r) 
          return INT_MAX;
      if (l == tl && r == tr) {
          return t[v];
      }
      int tm = (tl + tr) / 2;
      int kl = bitWiseAnd(v*2, tl, tm, l, min(r, tm)) ;
      int kr = bitWiseAnd(v*2+1, tm+1, tr, max(l, tm+1), r);
      return kl&kr;
    }

    int minimumDifference(vector<int>& nums, int k) {
      n = nums.size();
      int l = 0,r = 0, mn =INT_MAX;
      int cur =nums[0];
      build(nums,1,0,n-1);

      while(l<=r && r<n){
        mn = min(mn,abs(k- bitWiseAnd(1,0,n-1, l,r)));
        if(l==r){
          ++r;
          continue;
        }
        if(r==n-1){
          ++l;
          continue;
        }

        int ls = abs(k- bitWiseAnd(1,0,n-1, l+1,r));
        int rs = abs(k- bitWiseAnd(1,0,n-1, l,r+1));

        if(ls<=rs){
          ++l;
        }
        else{
          ++r;
        }
      }

      return mn;
    }
};
