class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        vector<int> v(32,(int)1e9+5);
        vector<vector<pair<int,int>>> a(nums.size(),vector<pair<int,int>>(32));
        for(int i=nums.size()-1;i>=0;i--){
            for(int j=0;j<32;j++){
                if((nums[i]>>j)&1){
                    a[i][j]={v[j],j};
                }
                else{
                    a[i][j]={i,j};
                    v[j]=i;
                }
            }
        }
        for(vector<pair<int,int>>& l:a) {sort(l.begin(),l.end());
                                        // for(int j=0;j<l.size();j++){
                                        //     cout<<l[j].first<<" "<<l[j].second<<"\n";
                                        // }
                                        }
        int ans=(int)1e9+5;
        for(int i=0;i<nums.size();i++){
            ans=min(abs(k-nums[i]),ans);
            if(nums[i]<=k) continue;
            else{
                int x=nums[i];
                for(int j=0;j<a[i].size();j++){
                    if(a[i][j].first==(int)1e9+5) break;
                     if(a[i][j].first==i) continue;
                    // cout<<nums[i]<<" "<<a[i][j].first<<" "<<a[i][j].second<<"\n";
                    x-=(1<<a[i][j].second);
                    while(j+1<a[i].size()&&a[i][j].first==a[i][j+1].first) {
                        j++;
                        x-=(1<<a[i][j].second);
                    }
                    // cout<<nums[i]<<" "<<x<<"\n";
                       ans=min(abs(k-x),ans);
                    if(x<=k) break;
                }
            }
        }
        return ans;
    }
};
