class Solution {
public:
    int fun(vector<int> & bits){
        int res =0;
        for(int k=0;k<32;k++){
            if(bits[k]==0){
                res^=(1<<k);
            }
        }
        return res;
    }
    int minimumDifference(vector<int>& nums, int k) {
        vector<int> bits(32,0);
        int i=0; int ans = INT_MAX;
        for(int j=0;j<nums.size();j++){
            for(int k=0;k<32;k++){
                if(((nums[j]>>k)&1)==0){
                    bits[k]+=1;
                }
            }
            int x = fun(bits);
            ans = min(ans,abs(x-k));
            while(i<j && x<k){
                for(int k=0;k<32;k++){
                    if(((nums[i]>>k)&1)==0){
                        bits[k]-=1;
                    }
                }
                i++;
                x=fun(bits);
                ans = min(ans,abs(x-k)); 
            }
        }
        return ans;
    }
};
