class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int i=0,j=0,ans=INT_MAX,n=nums.size();
        vector<int>v(31);
        while(j<=n){
            if(j<=i){
                if(j<n){
                    for(int x=0;x<31;x++){
                        v[x]+=(nums[j]>>x)&1;
                    }
                }
                j++;
                continue;
            }
            int val=0;
            int l=j-i;
            for(int x=0;x<31;x++){
                if(v[x]==l)
                    val|=(1<<x);
            }
            ans=min(ans,abs(val-k));
            if(val<k){
                for(int x=0;x<31;x++)
                    v[x]-=(nums[i]>>x)&1;
                i++;
                continue;
            }
            if(j<n){
                for(int x=0;x<31;x++){
                    v[x]+=(nums[j]>>x)&1;
                }
            }
            j++;
        }
        return ans;
    }
};
