class CustomSegmentTree {
    private int[] tree;
    private int size;

    public void init(int[] arr) {
        size = arr.length;
        tree = new int[4 * size];
        construct(arr, 0, 0, size - 1);
    }

    private void construct(int[] arr, int node, int start, int end) {
        if (start == end) {
            tree[node] = arr[start];
        } else {
            int mid = (start + end) / 2;
            construct(arr, 2 * node + 1, start, mid);
            construct(arr, 2 * node + 2, mid + 1, end);
            tree[node] = tree[2 * node + 1] & tree[2 * node + 2];
        }
    }

    public int query(int left, int right) {
        return rangeAndQuery(0, 0, size - 1, left, right);
    }

    private int rangeAndQuery(int node, int start, int end, int left, int right) {
        if (right < start || end < left) {
            return Integer.MAX_VALUE;
        }
        if (left <= start && end <= right) {
            return tree[node];
        }
        int mid = (start + end) / 2;
        int leftResult = rangeAndQuery(2 * node + 1, start, mid, left, right);
        int rightResult = rangeAndQuery(2 * node + 2, mid + 1, end, left, right);
        return leftResult & rightResult;
    }
}

class Solution {
    public int minimumDifference(int[] nums, int k) {
        int size = nums.length;
        CustomSegmentTree segmentTree = new CustomSegmentTree();
        segmentTree.init(nums);
        int minDifference = Integer.MAX_VALUE;

        for (int i = 0; i < size; i++) {
            int left = i - 1, right = size - 1;
            while (right - left > 1) {
                int mid = (left + right) / 2;
                if (segmentTree.query(i, mid) >= k) {
                    left = mid;
                } else {
                    right = mid;
                }
            }
            if (left != i - 1) {
                minDifference = Math.min(minDifference, Math.abs(segmentTree.query(i, left) - k));
            }
            minDifference = Math.min(minDifference, Math.abs(k - segmentTree.query(i, right)));
        }
        return minDifference;
    }
}
