class Solution {
public:

    int a[100005];
    int n;
    vector<int> b[34];


    int minimumDifference(vector<int>& nums, int k) {

        n=nums.size();

        for(int i=0;i<nums.size();i++)
            a[i+1]=nums[i];


        for(int j=0;j<=30;j++)
        b[j].push_back(n+1);

    for(int i=n;i>=1;i--){

        for(int j=30;j>=0;j--){
            if((a[i]&(1<<j)) ==0)
                b[j].push_back(i);
        }
    }

    // cout<<"hlo\n";
    // for(int i=0;i<=4;i++)
    // {
    //     cout<<"i is "<<i<<" :";
    //     for(auto x:b[i])
    //         cout<<x<<" ";
    //     cout<<'\n';
    // }

    long long ans=1e9;

    for(int i=1;i<=n;i++){
        map<int,long long> mp;
        long long cur=0;
        for(int j=0;j<=30;j++)
        {    while(b[j].back()<i)
                b[j].pop_back();

            mp[b[j].back()]=mp[b[j].back()] | (1<<j);

            cur|=(1<<j);
        }

        for(auto [x,y]:mp){
            if(x<=n){
            cur^=y;

            ans=min(ans,abs(cur-k));}

        }

    }        
return ans;    }
};
