class Solution {
public:
    int minimumDifference(vector<int>& a, int k) {
        int n=a.size();
        vector<vector<int>> next(n+1,vector<int>(31,n));
        for(int i=n-1;i>=0;i--) {
            next[i]=next[i+1];
            for(int bit=0;bit<31;bit++) {
                if(!(a[i]>>bit&1)) {
                    next[i][bit]=i;
                }
            }
        }

        int ans=1e9;
        for(int i=0;i<n;i++) {
            int cur=a[i],j=i;
            while(j<n && cur) {
                cur&=a[j];
                ans=min(ans,abs(cur-k));
                int nj=n;
                for(int bit=0;bit<32;bit++) {
                    if(cur>>bit&1) {
                        nj=min(nj,next[j][bit]);
                    }
                }
                j=nj;
            }
        }

        return ans;
    }
};
