class Solution {
public:
    int bits[32][100005];
    int Ad(int l, int r)
    {
        int ans = 0;
        for (int i = 0; i < 32; i++) {
            int x;
            if (l == 0)
                x = bits[i][r];
            else
                x = bits[i][r]
                    - bits[i][l - 1];
            if (x == r - l + 1)
                ans = (ans | (1 << i));
        }
        return ans;
    }
    int minimumDifference(vector<int>& nums, int k) {
        int n=nums.size();
        vector<int> v(nums.begin(),nums.end()); int ans=INT_MAX;
        int n1=nums.size();
        for(int i=0;i<32;i++){
            for(int j=0;j<n1;j++){
                if(j==0){
                    bits[i][j]=((v[j]>>i)&1);
                }
                else{
                     bits[i][j]=((v[j]>>i)&1);
                    bits[i][j]=bits[i][j-1] + ((v[j]>>i)&1);
                }
            }
        }
        for(int i=0;i<v.size();i++){
            int lo=i; int hi=n-1; int l=i;
            while(hi-lo>1){
                int mid=(lo+hi)/2;
                int x=Ad(l,mid);
                if(x<=k){
                    hi=mid;
                }
                else{
                    lo=mid;
                }
            }
            if(hi>i){
                ans=min(ans,abs(Ad(i,hi)-k));
                ans=min(ans,abs(Ad(i,lo)-k));
            }
            else{
                ans=min(ans,abs(Ad(i,lo)-k));
            }
        }
        return ans;
    }
};
