class Solution {
public:
    int minimumDifference(std::vector<int>& nums, int k) {
        int zeros[32] = {0};
        auto minus = [&zeros](int x) {
            for (int i = 0; i < 32; ++i) {
                if (((x >> i) & 1) == 0) {
                    zeros[i] -= 1;
                }
            }
        };
        auto plus = [&zeros](int x) {
            for (int i = 0; i < 32; ++i) {
                if (((x >> i) & 1) == 0) {
                    zeros[i] += 1;
                }
            }
        };
        auto eval = [&zeros]() {
            int res = 0;
            for (int i = 0; i < 32; ++i) {
                if (zeros[i] == 0) {
                    res ^= (1 << i);
                }
            }
            return res;
        };
        int i = 0;
        int n = nums.size();
        int ans = INT_MAX;
        for (int j = 0; j < n; ++j) {
            plus(nums[j]);
            int x = eval();
            ans = std::min(ans, abs(x - k));
            while (i < j && x < k) {
                minus(nums[i]);
                ++i;
                x = eval();
                ans = std::min(ans, abs(x - k));
            }
        }
        return ans;
    }
};
