class Solution {

    void addFrq(int n,auto &frq){

        int r = 1;

        for(int i=0;i<31;i++){

            int p = (int)((n & r) > 0);
            frq[p][i]++;

            if(i < 30)
            r = r << 1;
        }

    }

    int getVal(auto &frq){

        int val = 0;
        int r = 1;

        for(int i=0;i<31;i++){

            if( frq[0][i] == 0 && frq[1][i] > 0)
                val += r;           

            if( i<30 )
                r = r << 1;
        }

        return val;
    }

    void removeFrq(int n,auto &frq){

         int r = 1;

        for(int i=0;i<31;i++){

            int p = (int)((n & r) > 0);

            frq[p][i]--;

            if(i < 30)
            r = r << 1;
        }

    }

public:
    int minimumDifference(vector<int>& nums, int k) {

        vector<vector<int>> frq(2,vector<int>(32,0));


        int ans = INT_MAX;
        int curr = (1<<29) - 1;        

        int l = 0,r = 0;

        int n = nums.size();

        while( r < n ) {

            addFrq(nums[r],frq); // anding

            curr = getVal(frq);
            ans = min(ans,abs(curr-k));

            while(curr < k && r != l) {
                removeFrq(nums[l],frq);                
                curr = getVal(frq);
                ans = min(ans,abs(curr-k));
                l++;
            }

            ans = min(ans,abs(curr-k));

            r++;

        }


        while(l < (n-1)) {
            removeFrq(nums[l],frq);                
            curr = getVal(frq);
            ans = min(ans,abs(curr-k));
            l++;
        }


        return ans;

    }
};
