class Solution {
    function minimumDifference($oxygenLevels, $target) {
        $current_AND_value = $oxygenLevels[0];
        $best_diff = abs($current_AND_value - $target);

        $window_bit_counts = array_fill(0, 32, 0);
        $num = $oxygenLevels[0];
        for ($j = 0; $j < 32; $j++) {
            if ($num & (1 << $j)) {
                $window_bit_counts[$j]++;
            }
        }

        $left_idx = 0;
        $right_idx = 0;
        $size = count($oxygenLevels);
        while ($right_idx < $size) {
            if ($current_AND_value > $target || $left_idx > $right_idx) {
                $right_idx++;
                if ($right_idx >= $size) {
                    break;
                }
                $num = $oxygenLevels[$right_idx];
                for ($j = 0; $j < 32; $j++) {
                    if ($num & (1 << $j)) {
                        $window_bit_counts[$j]++;
                    }
                }
            } else {
                $num = $oxygenLevels[$left_idx];
                for ($j = 0; $j < 32; $j++) {
                    if ($num & (1 << $j)) {
                        $window_bit_counts[$j]--;
                    }
                }
                $left_idx++;
            }

            $current_AND_value = 0;
            foreach ($window_bit_counts as $bit => $count) {
                if ($count == $right_idx - $left_idx + 1) {
                    $current_AND_value += (1 << $bit);
                }
            }

            if ($left_idx <= $right_idx) {
                $best_diff = min($best_diff, abs($current_AND_value - $target));
            }
        }

        return $best_diff;
    }
}
