class Solution {
public:
    vector<int> solve(int x) {
        vector<int> res;
        for(int i=0;i<32;i++) {
            if(x&(1<<i)) {
                res.push_back(i);
            }
        }
        return res;
    }
    int compute(vector<int>& bits, int sz) {
        int res=0;
        for(int i=0;i<bits.size();i++) {
            if(bits[i]==sz) {
                res|= (1<<i);
            }
        }
        return res;
    }
    int minimumDifference(vector<int>& nums, int k) {

       vector<int> bits(32,0);
                        vector<int> converted = solve(nums[0]);
                for(int i:converted) {
                    bits[i]++;
                }

        int curr = nums[0];
        int res = abs(k-curr);
        int left=0, right=0;
        while(left<nums.size() and right<nums.size()) {
            if(left>right or curr>k) {
                right++;
                if(right==nums.size()) {
                    break;
                }
                vector<int> converted = solve(nums[right]);
                for(int i:converted) {
                    bits[i]++;
                }
            } else {
                vector<int> converted = solve(nums[left]);
                for(int x: converted) {
                    if(bits[x]>0) {
                        bits[x]--;
                    }
                }
                left++;
            }
            if(left<=right) {
            curr = compute(bits, right-left+1);

                res = min(res, abs(k-curr));

            }
        }
        return res;
    }
};
