class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int n = nums.size();
        int min_diff = INT_MAX;
        for(int& i:nums){
            min_diff=min(min_diff, abs(k-i));
        }
        unordered_set<int> curr, nxt;

        for (int i = 0; i < n; ++i) {
            nxt.clear();
            nxt.insert(nums[i]);

            for (int val : curr) {
                nxt.insert(val & nums[i]);
            }

            for (int val : nxt) {
                min_diff = min(min_diff, abs(k - val));
            }

            swap(curr, nxt);
        }

        return min_diff;

    }
};
