class Solution {
public:

    vector<int> zeros;

    void sub(int x)
    {
        for (int i = 0; i < 32; ++i) 
        {
            if (((x >> i) & 1)==0) 
            {
                zeros[i] -= 1;
            }
        }
    }

    void add(int x)
    {
        for (int i = 0; i < 32; ++i) {
            if (((x >> i) & 1) == 0) {
                zeros[i] += 1;
            }
        }
    }

    int f()
    {
        int res = 0;
        for (int i = 0; i < 32; ++i) {
            if (zeros[i] == 0) {
                res ^= (1 << i);
            }
        }
        return res;
    }

    int minimumDifference(vector<int>& nums, int k) {

        int n=nums.size(),i=0;
        int ans=INT_MAX;

        zeros.resize(32,0);

        for(int j=0;j<n;j++) 
        {
            add(nums[j]);

            int x=f();

            ans=min(ans,abs(x - k));

            while(i<j && x<k) 
            {
                sub(nums[i]);
                i++;
                x=f();

                ans=min(ans,abs(x - k));
            }

        }

        return ans;

    }
};
